import React from 'react'

const Pagination = ({tabledata, nPages, currentPage, setCurrentPage}) => {

    console.log(tabledata)

    const nextPage = () => {
        if(currentPage !== nPages) 
            setCurrentPage(currentPage + 1)
    }
    const prevPage = () => {
        if(currentPage !== 1) 
            setCurrentPage(currentPage - 1)
    }

    const pageNumbers = [...tabledata(nPages + 1).keys()].slice(1)
    console.log(pageNumbers)

    console.log(tabledata)
    return (

        <>
        <table className='table'>
            <thead>
                <tr>
                    <th>Name</th>
                    <th>Gender</th>
                    <th>Email</th>
                    <th>Mobile no.</th>
                    <th>Occupation</th>

                </tr>
            </thead>

            <tbody>

                {tabledata.map(item => {
                        return (
                        <tr>
                            <td>
                                {item.Name}

                            </td>
                            <td>
                                {item.Gender}
                            </td>
                            <td>
                                {item.Email}
                            </td>
                            <td>
                                {item.Mobile}
                            </td>
                            <td>
                                {item.Occupation}
                            </td>
                        </tr>

)
                    })}

            </tbody>

        </table>


<nav>

    <ul className='pagination justify-content-center'>

        <li className='page-item'>

            <a className='page-link'
            onClick={prevPage}
            href='#'
            >
Previous
            </a>

             </li>
    </ul>

</nav>

</>
    )

}

export default Pagination
