import './App.css';
import Pagination from './pagination/Pagination';
import data from "./data.json"
import { useState } from 'react';

function App() {
  // User is currently on this page
const [currentPage, setCurrentPage] = useState(1)

// No of Records to be displayed on each page   
const [recordsPerPage ,setRecordsPage] = useState(25);

const indexOfLastRecord = currentPage * recordsPerPage;
const indexOfFirstRecord = indexOfLastRecord - recordsPerPage;

const currentRecords = data.slice(indexOfFirstRecord, 
  indexOfLastRecord);

console.log(currentRecords , 'dsdsd')
console.log(recordsPerPage , 'recordsPerPage')

const nPages = Math.ceil(data.length / recordsPerPage)

  return (
    <div>
      <Pagination
      tabledata={currentRecords}
      nPages = { nPages }
      currentPage = { currentPage }
      setCurrentPage = { setCurrentPage }
      setRecordsPage = { setRecordsPage}
      />

                <select onClick={(e)=> setRecordsPage(e.target.value)} class="form-select" aria-label="Default select example" style={{ width: 200, justifyContent: 'space-around' }}>

                    <option selected value="25">25</option>
                    <option value="50">50</option>
                    <option value="100">100</option>
                </select>

    </div>
  );
}

export default App;
