import React from 'react'

const Pagination = ({ tabledata, nPages, currentPage, setCurrentPage }) => {

    console.log(tabledata)

    const nextPage = () => {
        if (currentPage !== nPages)
            setCurrentPage(currentPage + 1)
    }
    const prevPage = () => {
        if (currentPage !== 1)
            setCurrentPage(currentPage - 1)
    }

    const pageNumbers = [...Array(nPages + 1).keys()].slice(1)
    console.log(pageNumbers)


    console.log(tabledata)
    return (

        <>
            <table className='table'>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Gender</th>
                        <th>Email</th>
                        <th>Mobile no.</th>
                        <th>Occupation</th>

                    </tr>
                </thead>

                <tbody>

                    {tabledata.map(item => {
                        return (
                            <tr>
                                <td>
                                    {item.Name}

                                </td>
                                <td>
                                    {item.Gender}
                                </td>
                                <td>
                                    {item.Email}
                                </td>
                                <td>
                                    {item.Mobile}
                                </td>
                                <td>
                                    {item.Occupation}
                                </td>
                            </tr>

                        )
                    })}

                </tbody>

            </table>


            <nav style={{ display: 'flex', justifyContent: 'space-around' }} >

                <select onClick={(e)=> console.log(e.target.value)} class="form-select" aria-label="Default select example" style={{ width: 200, justifyContent: 'space-around' }}>
                    <option selected placeholder='Select no. of pages'>Select No. of Pages</option>
                    <option value="1">25</option>
                    <option value="2">50</option>
                    <option value="3">100</option>
                </select>


                <ul className='pagination justify-content-center'>

                    <li className='page-item'>

                        <a className='page-link'
                            onClick={prevPage}
                            href='#'
                        >
                            Previous
                        </a>

                    </li>

                    {pageNumbers.map(pgNumber => {

                        return (

                            <li key={pgNumber} className={`page-item ${currentPage == pgNumber ? 'active' : ''} `}>

                                <a onClick={() => setCurrentPage(pgNumber)}
                                    className='page-link'
                                    href='#'>
                                    {pgNumber}
                                </a>


                            </li>
                        )

                    })}


                    <li className='page-item'>

                        <a className='page-link' onClick={nextPage} href='#'>
                            Next

                        </a>
                    </li>
                </ul>

            </nav>



        </>
    )

}

export default Pagination
